odoo.template:
'use strict';
const CACHE_NAME = '';
        const DYNAMIC_CACHE_NAME = 'dynamic-' + '';
const FILES_TO_CACHE = [

      '',

];

// cache size limit function
const limitCacheSize = (name, size) => {
    caches.open(name).then(cache => {
        cache.keys().then(keys => {
            if(keys.length > size){
                cache.delete(keys[0]).then(limitCacheSize(name, size));
            }
        });
    });
};

self.addEventListener('install', function (evt) {
  console.log('[ServiceWorker] Install');
  evt.waitUntil(
   caches.open(CACHE_NAME).then(function (cache) {
       console.log('[ServiceWorker] Pre-caching offline page');
       return cache.addAll(FILES_TO_CACHE);
   })
  );
  self.skipWaiting();
});

self.addEventListener('activate', function(evt) {
  console.log('[ServiceWorker] Activate');
   evt.waitUntil(
       caches.keys().then(function(keyList) {
           return Promise.all(keyList.map(function(key) {
               if (key !== CACHE_NAME) {
                   console.log('[ServiceWorker] Removing old cache', key);
                   return caches.delete(key);
               }
           }));
       })
   );
  self.clients.claim();
});

self.addEventListener('fetch', function(evt) {
    // if (evt.request.cache === 'only-if-cached' && evt.request.mode !== 'same-origin') {
    //   return;
    // }
    console.log('[ServiceWorker] Fetch', evt.request.url);
    evt.respondWith(
       caches.match(evt.request)
        .then(cacheRes => {
            return cacheRes || fetch(evt.request).then(fetchRes => {
                return caches.open(DYNAMIC_CACHE_NAME).then(cache => {
                    cache.put(evt.request.url, fetchRes.clone());
                    // check cached items size
                    limitCacheSize(DYNAMIC_CACHE_NAME, 50);
                    return fetchRes;
                });
            })
            .catch(() => {
                console.log('[ServiceWorker] Offline fetch event')
                return caches.match('/offline-fallback');
            });
        })
    );
});

